    async function makeSwap() {
        try {
            if (ethereum) {
                const { amount } = inputData;
                const provider = new ethers.providers.Web3Provider(ethereum);
                const signer = provider.getSigner();
                //const bigamount = amount * (10 ** 18);
                const hmSwapContract = new ethers.Contract(stakeContractAddress, stakeContractAbi, signer);
                const hmsWithSigner = hmSwapContract.connect(signer);
                const swap = await hmsWithSigner.makeSwap(fromTokenAddress, toTokenAddress, amount);
                //setIsLoading(true);
                await swap.wait();
                //setIsLoading(false);
                window.location.reload()
            } else {
                console.log("no ethereum object");
            }
        } catch (error) {
            console.log(error);
            throw new Error("no ethereum object dummy");
        }

    }


    async function calculateOutput() {
        const { amount } = inputData;
        if (amount === "") {
            setOutputData("Output Amount...");
        } else {
            const hmSwapContract = createSwapContract();
            const largeOutput = await hmSwapContract.calculateReturn(fromTokenAddress, toTokenAddress, amount);
            setOutputData(Number(largeOutput));
        }
    }



