    async function approveSwap() {
        try {
            if (ethereum) {
                const { amount } = inputData;
                //const bigamount = BigNumber.from(amount * (10 ** 18));
                const provider = new ethers.providers.Web3Provider(ethereum);
                const signer = provider.getSigner();
                const tokenContract = new ethers.Contract(fromTokenAddress, tokenContractAbi, signer);
                const approval = await tokenContract.approve(swapContractAddress, amount);
                setIsLoading(true);
                await approval.wait();
                setIsLoading(false);
                setIsApproved(true)
            } else {
                console.log("no ethereum object");
            }
        } catch (error) {
            console.log(error);
            throw new Error("no ethereum object");
        }

    }



    async function makeSwap() {
        try {
            if (ethereum) {
                const { amount } = inputData;
                //const bigamount = BigNumber.from(amount * (10 ** 18));
                const hmSwapContract = createSwapContract();
                const swap = await hmSwapContract.swap(fromTokenAddress, toTokenAddress, amount);
                setIsLoading(true);
                await swap.wait();
                setIsLoading(false);
                setIsApproved(false)
                window.location.reload()
            } else {
                console.log("no ethereum object");
            }
        } catch (error) {
