import React from 'react'
import ethicon from "../images/ethicon.png"
import hmticon from "../images/hmticon.png"
import daiicon from "../images/daiicon.png"
import { Modal } from "react-bootstrap"


function Swap() {
    const [modalShow, setModalShow] = React.useState(false);
    const [fromTokenSelected, setFromTokenSelected] = React.useState("ETH");
    const [toTokenSelected, setToTokenSelected] = React.useState("HMT");
    const [isFrom, setIsFrom] = React.useState(true);
    const [fromTokenAddress, setFromTokenAddress] = React.useState("eth addy");
    const [toTokenAddress, setToTokenAddress] = React.useState("hmt addy");
    const [inputData, setInputData] = React.useState({ amount: "" });
    const [ouputData, setOutputData] = React.useState("Output Amount...");






    const TokenCard = ({ icon, name }) => (
        <div className="container">
            <div className='row justify-content-center align-items-center'>
                <div className='col col-4'>
                    <img src={icon} alt="ethy" />
                </div>
                <div className='col col-4 mt-2'>
                    <h3>{name}</h3>
                </div>
            </div>
        </div>
    );

    function selectToken(token) {
        isFrom ? setFromTokenSelected(token) : setToTokenSelected(token)
        isFrom ? getFromAddress(token) : getToAddress(token)
        setModalShow(false)
    }

    function getFromAddress(token) {
        if (token === "ETH") {
            setFromTokenAddress("eth addy")
        } else if (token === "DAI") {
            setFromTokenAddress("dai addy")
        } else {
            setFromTokenAddress("hmt addy")
        }
    }

    function getToAddress(token) {
        if (token === "ETH") {
            setToTokenAddress("eth addy")
        } else if (token === "DAI") {
            setToTokenAddress("dai addy")
        } else {
            setToTokenAddress("hmt addy")
        }
    }

    function openFromModal() {
        setIsFrom(true);
        setModalShow(true);
    }

    function OpenToModal() {
        setIsFrom(false);
        setModalShow(true);
    }


    function ChooseTokenModal(props) {
        return (
            <div>
                <Modal
                    {...props}
                    size="lg"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered
                >
                    <div class="modal-header">
                        <h5 class="modal-title" id="staticBackdropLabel">Modal title</h5>
                        <button type="button" class="btn-close" onClick={props.onHide} aria-label="Close"></button>
                    </div>
                    <div class="modal-body">
                        <div className="d-grid gap-2 mx-2 mb-2">
                            <button className="btn btn-dark" onClick={() => selectToken("ETH")} >
                                <TokenCard
                                    icon={ethicon}
                                    name="Ether" />
                            </button>
                            <button className="btn btn-dark" onClick={() => selectToken("DAI")}>
                                <TokenCard
                                    icon={daiicon}
                                    name="Dai" />
                            </button>
                            <button className="btn btn-dark" onClick={() => selectToken("HMT")}>
                                <TokenCard
                                    icon={hmticon}
                                    name="Hire Me Token" />
                            </button>
                        </div>
                    </div>
                    <div class="modal-footer">
                        <button type="button" class="btn btn-secondary" onClick={props.onHide}>Close</button>

                    </div>
                </Modal>
            </div>
        );
    }

    function calcOuput() {
        const { amount } = inputData;
        setOutputData(Number(amount));
    }
    function totalChange() {
        handleChange()
        calcOuput();
    }


    const handleChange = (e) => {
        const { id, value } = e.target;
        setInputData((prevState) => {
            return {
                ...prevState,
                [id]: value,
            };
        });

    };
    /*
        const handleSubmit = (e) => {
            const { amount } = inputData;

            e.preventDefault();

            if (!amount) return;

            //sendTransaction();
            // update output
            // use use effect to reload everytime input changes
        };

    */



    return (
        <div className='Swap-bg p-5'>
            <div className='container rounded p-4'>
                <div className='row justify-content-center'>
                    <div className='col col-md-6'>
                        <div className='bg-dark p-3 rounded'>
                            <h3 className='text-light text-start my-1 ms-3'>Swap</h3>
                            <div className='container'>
                                <div className='col'>
                                    <div className='row row-3 my-3 bg-secondary rounded p-3'>
                                        <div className='container'>
                                            <div className='row align-items-center justify-content-between'>
                                                <div className='col col-8'>
                                                    <input type="number" class="form-control" id="amount" placeholder="Amount" onChange={totalChange} />
                                                </div>
                                                <div className='col col-4 mt-2'>
                                                    <div class="d-grid gap-2 mx-2 mb-2">
                                                        <button className='btn btn-light' onClick={() => openFromModal()}>
                                                            {fromTokenSelected}
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                    <div className='row row-3 my-3 bg-secondary rounded p-3'>
                                        <div className='container'>
                                            <div className='row align-items-center justify-content-between'>
                                                <div className='col col-8 mt-2'>
                                                    <div className="d-grid gap-2 mx-2 mb-2">
                                                        <button className='btn btn-light text-secondary text-start'>
                                                            {ouputData}
                                                        </button>
                                                    </div>
                                                </div>
                                                <div className='col col-4 mt-2'>
                                                    <div className="d-grid gap-2 mx-2 mb-2">
                                                        <button className='btn btn-light' onClick={() => OpenToModal()}>
                                                            {toTokenSelected}
                                                        </button>
                                                    </div>

                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div class="d-grid gap-2 mx-2 mb-2">
                                <button class="btn btn-secondary" type="button" onClick={() => console.log(inputData)}>Swap</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <ChooseTokenModal
                show={modalShow}
                onHide={() => setModalShow(false)}
            />
        </div>

    )
}

export default Swap
