    async function connectToMetamask() {
        if (typeof window.ethereum !== 'undefined') {
            try {
                await ethereum.request({ method: "eth_requestAccounts" });
                setAccounts(await ethereum.request({ method: "eth_accounts" }));
                console.log(accounts);
                setIsConnected(true);
            } catch (e) {
                console.log(e);
            }

        }
    }

    async function checkIfWalletIsConnect() {
        if (accounts.length > 0) {
            setIsConnected(true);
        } else {
            setIsConnected(false);
        }

    }

    async function makeSwap() {
        try {
            if (ethereum) {
                const { amount } = inputData;
                const bigamount = amount * (10 ** 18)
                const hmSwapContract = createSwapContract();
                const swap = await hmSwapContract.calculateReturn(fromTokenAddress, toTokenAddress, bigamount);
                //setIsLoading(true);
                await swap.wait();
                // setIsLoading(false);
                window.location.reload()
            } else {
                console.log("no ethereum object");
            }
        } catch (error) {
            console.log(error);
            throw new Error("no ethereum object");
        }

    }


    async function calculateOutput() {
        const { amount } = inputData;
        if (amount === "") {
            setOutputData("Output Amount...");
        } else {
            const hmSwapContract = createSwapContract();
            const largeOutput = await hmSwapContract.calculateReturn(fromTokenAddress, toTokenAddress, amount);
            setOutputData(Number(largeOutput));
        }
    }



    const handleChange = (e) => {
        const { id, value } = e.target;
        setInputData((prevState) => {
            return {
                ...prevState,
                [id]: value,
            };
        });


    };


