        };

    */

    React.useEffect(() => {
        checkIfWalletIsConnect();
    });

    React.useEffect(() => {
        calculateOutput()
    }, [inputData]);



    return (
        <div className='Swap-bg p-5'>
            <div className='container rounded p-4'>
                <div className='row justify-content-center'>
