                                    <button className="btn btn-primary" type="button" onClick={connectToMetamask}>
                                        <p className='mt-1'>Connect Wallet</p>
                                    </button>
                                    :
                                    <button className="btn btn-secondary" type="button" disabled>
                                        <p className='mt-1'>Wallet Connected</p>
                                    </button>
                                }
                            </div>
                        </div>
